var test = {
	chance_1: randomChars.get(),
	chance_2: randomChars.get(3),
	chance_3: randomChars.get(16, 'haha'),
	chance_4: randomChars.get({
		amount: 3
	}),
	chance_5: randomChars.get({
		chars: 'haha'
	}),
	chance_6: randomChars.get({
		amount: 36,
		use: {
			digits: false
		}
	}),
	chance_7: randomChars.get({
		amount: 36,
		use: {
			lower: false
		}
	}),
	chance_8: randomChars.get({
		amount: 36,
		use: {
			upper: false
		}
	})
},
	listEl = document.getElementById('test');

for(var key in test) {
	console.log(key + ': ' + test[key]);
	listEl.innerHTML += '<li><b>' + key + ':</b> ' + test[key] + '</li>';
}
